sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var aoaCount = 0;

	//Query for parent record
    var marketShareReport = new GlideRecord('x_ebh_bill_report_built_reports');
    marketShareReport.get(current.getValue('bill_report_sys_id'));

	//Set initial value for aoaCount to the EB AOA Count on parent record
    aoaCount = parseInt(marketShareReport.getValue('eb_aoa_count'));

	//Query all child competitor AOA records and add the competitor AOA count from each to aoaCount
    var competitorAOA = new GlideRecord('x_ebh_bill_report_competitors_aoa');
    competitorAOA.addQuery('bill_report_sys_id', marketShareReport.getUniqueValue());
    competitorAOA.query();
    while (competitorAOA.next()) {
        aoaCount += parseInt(competitorAOA.getValue('competitor_aoa_count'));
    }

	//Set the value for the Total AOA Count on the parent record and update
    marketShareReport.setValue('total_aoa_count', aoaCount);
	marketShareReport.update();

})(current, previous);
