sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {


    var compaoa = [];
    var compaoa1 = [];

    //Query for parent record
    var marketShareReport = new GlideRecord('x_ebh_bill_report_market_share_csv');
    marketShareReport.addQuery('u_mrkt_share_sys_id', current.sys_id);
    marketShareReport.query();

    if (marketShareReport.next()) {
        marketShareReport.account_name = current.account_client_name;
        marketShareReport.u_mrkt_share_sys_id = current.sys_id;
        marketShareReport.account_number = current.account_number;
        marketShareReport.account_type = current.account_type;
        marketShareReport.branch = current.branch;
        marketShareReport.market = current.market;
        marketShareReport.total = current.u_total;
        marketShareReport.eb_aoa_count = current.eb_aoa_count;
        marketShareReport.competitor_name = current.competitor_names;
        marketShareReport.comp_aoa = current.competitor_counts;


        var compname = current.competitor_names.toString().split('\n');
        var aoac = current.competitor_counts.toString().split('\n');
        for (var i = 0; i < compname.length; i++) {

            var cmpn = compname[i].trim();
            var aoad = aoac[i].trim();
            compaoa.push(cmpn + '=' + aoad);
        }

        var l = compaoa.join('\n');

        marketShareReport.competitor_aoa_count = l;


        marketShareReport.update();
    } else {
        marketShareReport.initialize();
        marketShareReport.account_name = current.account_client_name;
        marketShareReport.u_mrkt_share_sys_id = current.sys_id;
        marketShareReport.account_number = current.account_number;
        marketShareReport.account_type = current.account_type;
        marketShareReport.branch = current.branch;
        marketShareReport.market = current.market;
        marketShareReport.total = current.u_total;
        marketShareReport.eb_aoa_count = current.eb_aoa_count;
        marketShareReport.competitor_name = current.competitor_names;
        marketShareReport.comp_aoa = current.competitor_counts;

        var compname1 = current.competitor_names.toString().split('\n');
        var aoac1 = current.competitor_counts.toString().split('\n');
        for (var j = 0; j < compname1.length; j++) {

            var cmpn1 = compname1[j].trim();
            var aoad1 = aoac1[j].trim();
            compaoa1.push(cmpn1 + '=' + aoad1);
        }

        var l1 = compaoa1.join('\n');

        marketShareReport.competitor_aoa_count = l1;
        marketShareReport.insert();
    }

})(current, previous);
