sys_ux_client_script.script:
/**
 * Updates the total AOA count on a Glide form by summing up the AOA counts of all competitors and adding it to the count of the current record.
 * @param {Object} params - An object containing the necessary parameters for the function.
 * @param {Object} params.api - The Glide API object.
 * @param {Object} params.event - The Glide event object.
 * @param {Object} params.helpers - The Glide helpers object.
 * @param {Object} params.imports - The Glide imports object.
 */
function handler({ api, event, helpers, imports }) {
  // Get the current Glide form
  const g_form = api.data.glide_form_1.nowRecordFormBlob.gForm;

  // Calculate the total AOA count of all competitors
  const total = calculateCompetitorAoaCountSum(api.state.competitors_data);

  // Get the AOA count of the current record
  const eb_aoa_count = Number(g_form.getValue("eb_aoa_count"));

  // Log the total and current AOA counts
  console.log({ total, eb_aoa_count });

  // Set the total AOA count on the form by adding the competitor count to the current count
  g_form.setValue("u_total", total + eb_aoa_count);

  // Return nothing
  return;

  /**
   * Calculates the total AOA count of all competitors.
   * @param {Array} arr - An array of objects containing competitor data.
   * @returns {number} - The total AOA count of all competitors.
   */
  function calculateCompetitorAoaCountSum(arr) {
    let sum = 0;
    for (let i = 0; i < arr.length; i++) {
      if (arr[i].competitor_aoa_count) {
        sum += Number(arr[i].competitor_aoa_count);
      }
    }
    return sum;
  }
}
