sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports 
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
  const { competitors_data } = api.state;
  const competitor_columns = createCompetitorObject(competitors_data);
  const g_form = api.data.glide_form_1.nowRecordFormBlob.gForm;
  console.log ({ competitors_data, competitor_columns});

  api.setState('competitor_columns', competitor_columns);

  g_form.setValue('competitor_names', competitor_columns.competitor);
  g_form.setValue('competitor_counts', competitor_columns.aoa_count);

  return;
  function createCompetitorObject(competitorArray) {
    const aoaCountArray = competitorArray.map(competitor => competitor.competitor_aoa_count);
    const competitorNameArray = competitorArray.map(competitor => competitor.competitor);

    const newObject = {
      aoa_count: aoaCountArray.join("\n"),
      competitor: competitorNameArray.join("\n")
    };

    return newObject;
  }


}
